mapper.select:
/** retrieveWrkList */
SELECT WRK_NM , SOURCE FROM ROBOT01 WHERE USER_ID = #{userId}
AND WRK_NM LIKE CONCAT('%', #{wrkNm}, '%')
ORDER BY WRK_NM

/* retrieveWrkList */
SELECT WRK_NM , SOURCE , DATE_FORMAT(CHNG_HISTRY_DTTM,'%Y-%m-%d %H:%i:%s') AS CHNG_HISTRY_DTTM FROM ROBOT02 WHERE USER_ID = #{userId}
AND WRK_NM LIKE CONCAT('%', #{wrkNm}, '%')
ORDER BY CHNG_HISTRY_DTTM DESC
