mapper.insert:
	/* view010101lite.insertViewDrawWrk */

	MERGE INTO ROBOT01
		USING DUAL
		ON USER_ID = #{userId}
		AND VIEW_ID = #{viewId}
	WHEN MATCHED THEN
		UPDATE SET CHNG_HISTRY_DTTM = SYSDATE
		         , SOURCE = #{source}
    WHEN NOT MATCHED THEN
    	INSERT VALUES (
			#{userId}
		  , #{viewId}
		  , SYSDATE
		  , NULL
		  , #{source})

/* view010101lite.insertDevSource*/

MERGE INTO ROBOT03
	USING DUAL
	ON USER_ID = #{userId}
    AND VIEW_ID = #{viewId}
    AND WRK_ID = #{WRK_ID} 

WHEN MATCHED THEN

	UPDATE SET QUERY = #{QUERY}
	     , TO_MAP = #{TO_MAP}
	     , FROM_MAP = #{FROM_MAP}

WHEN NOT MATCHED THEN

	INSERT VALUES (
		#{userId}
	  , #{viewId}
	  , #{WRK_ID}
	  , #{QUERY}
	  , #{FROM_MAP}
	  , #{TO_MAP}
	  , SYSDATE)

mapper.delete:
			/* view010101lite.deleteDevSource*/

			DELETE FROM ROBOT03
			WHERE USER_ID = #{userId}
			AND VIEW_ID = #{viewId}
			AND WRK_ID NOT IN


